mapper.update:
UPDATE SC_AUTH_COMPANY_INFO
SET APPROVE_STATE = #{APPROVE_STATE}, APPROVE_DATE= sysdate, POINT = #{POINT}, REASON = #{REASON}
WHERE COM_CODE = #{COM_CODE}
AND AUTH_TYPE = #{AUTH_TYPE}
AND AUTH_SEQ = #{AUTH_SEQ}

UPDATE SC_AUTH_COMPANY_INFO
SET APPROVE_STATE = #{APPROVE_STATE}, APPROVE_DATE= sysdate, POINT = #{POINT}
WHERE COM_CODE = #{COM_CODE}
AND AUTH_TYPE = #{AUTH_TYPE}

mapper.select:
SELECT COM_CODE, AUDIT_ID, AUTH_TYPE, TO_NUMBER(AA.POINT, '999999.0') AS POINT, AUDIT_COMMENT, AUTH_SEQ, REG_COM_USER_IDX, REG_DATE
FROM SC_AUDIT_ITEM_POINT
WHERE COM_CODE = #{COM_CODE} AND AUTH_TYPE = #{AUTH_TYPE}

SELECT ACI.COM_CODE, CC.COM_NAME, AUTH_TYPE, AUTH_SEQ, APPROVE_STATE, TO_CHAR(APPROVE_DATE, 'YYYY.MM.DD') AS APPROVE_DATE, TO_CHAR(SEND_DATE, 'YYYY.MM.DD') AS SEND_DATE, POINT FROM SC_AUTH_COMPANY_INFO ACI INNER JOIN SC_COMPANY_CODE CC ON CC.COM_CODE = ACI.COM_CODE WHERE 1 =1 AND AUTH_TYPE = #{AUTH_TYPE}
AND (ACI.COM_CODE LIKE CONCAT(CONCAT('%', #{COM_CODE}), '%'))
AND (CC.COM_NAME LIKE CONCAT(CONCAT('%', #{COM_NAME}), '%'))
