mapper.insert:
INSERT into SC_BASE_CONFIG(
    BASE_CONFIG_IDX
    ,GUBN
    ,CONFIG_CODE
    ,CONFIG_VALUE
    ,CONFIG_SUMMARY
    ,CONFIG_STATUS
    ,CONFIG_OPTION
    ,CONFIG_OPTION2
    ,CONFIG_OPTION3
    ,REG_DW_USER_IDX
    ,UP_DW_USER_IDX
    ,REG_DATE,UP_DATE
)
VALUES (
    SEQ_BASE_CONFIG.NEXTVAL
    ,#{GUBN}
    ,#{CONFIG_CODE}
    ,#{CONFIG_VALUE}
    ,#{CONFIG_SUMMARY}
    ,#{CONFIG_STATUS}
    ,#{CONFIG_OPTION}
    ,#{CONFIG_OPTION2}
    ,#{CONFIG_OPTION3}
    ,#{USER_IDX}
    ,#{USER_IDX}
    ,sysdate
    ,sysdate
)

mapper.update:
update SC_BASE_CONFIG set
    GUBN=#{GUBN}
    ,CONFIG_CODE=#{CONFIG_CODE}
    ,CONFIG_VALUE=#{CONFIG_VALUE}
    ,CONFIG_SUMMARY=#{CONFIG_SUMMARY}
    ,CONFIG_STATUS=#{CONFIG_STATUS}
    ,CONFIG_OPTION=#{CONFIG_OPTION}
    ,CONFIG_OPTION2=#{CONFIG_OPTION2}
    ,CONFIG_OPTION3=#{CONFIG_OPTION3}
    ,UP_DW_USER_IDX=#{USER_IDX}
    ,UP_DATE=sysdate
where
    BASE_CONFIG_IDX=#{IDX}
