mapper.select:

SELECT RN, COM_CODE, AUTH_TYPE, AUTH_CODE, AUTH_DATE, EXP_DATE, REG_DATE, ITEM_STATE
FROM
(
    SELECT /*+ INDEX_DESC(ISO_AUTH_COMPANY_ITEM PK_ISO_AUTH_COMPANY_ITEM) */
    ROWNUM RN, COM_CODE, AUTH_TYPE, AUTH_CODE, AUTH_DATE, EXP_DATE, REG_DATE, ITEM_STATE
    FROM ISO_AUTH_COMPANY_ITEM
    WHERE ROWNUM <= #{pageNum} * #{amount}
)
   WHERE RN > (#{pageNum}-1) * #{amount}
