WHERE SVHC_IDX =#{idx}

UPDATE SC_PART_MGMT_DECLARATION_DATA
SET DATA_GUBUN = #{DATA_GUBUN},CONFIRM_DATE = #{CONFIRM_DATE},APPLICABLE_NO = #{APPLICABLE_NO},NONE_APPLICABLE_NO=#{NONE_APPLICABLE_NO},WARRANTY_ITEM=#{WARRANTY_ITEM}


        ,FILE_PATH=#{FILE_PATH},FILE_NAME=#{FILE_NAME}



WHERE DECL_IDX =#{DECL_IDX}

UPDATE SC_PART_MGMT_DECLARATION_DATA
SET FILE_PATH='' ,FILE_NAME=''
WHERE DECL_IDX =#{idx}

